import { useState } from "react";
import Logements from "../../Logements";
import "./Carrousel.css";

function Carrousel(props) {
  const { img_carrousel } = props;
  const [carrouselIndex, updateCarrouselIndex] = useState(0);
  const nextSlide = () => {
    updateCarrouselIndex((prevIndex) => prevIndex + 1);
  };
  const previousSlide = () => {
    updateCarrouselIndex((prevIndex) => prevIndex - 1);
  };
  return (
    <div className="carrousel">
      <button onClick={previousSlide}>Précédent</button>
      <div className="slide">
        <img
          src={img_carrousel[carrouselIndex]}
          alt={img_carrousel[carrouselIndex]}
        />
      </div>
      <button onClick={nextSlide}>Suivant</button>
    </div>
  );
}

export default Carrousel;

