import React, { useState, useEffect } from "react";
import "./Collapse.css";

function Collapse(props) {
  const { titre_collapse, type_collapse, description_collapse } = props;
  const [ferme, setFerme] = useState(0);

  if (type_collapse === "Texte") {
    return (
      <>
        <div className="kasa-collapse">
          <h3 className="kasa-collapse-title">{titre_collapse}</h3>
          <button onClick={() => setFerme(ferme + 1)}>
            <div className="kasa-collapse-chevron">
              <i className="fa-solid fa-chevron-up"></i>
            </div>
          </button>
        </div>
        <div className="kasa-collapse-texte">
          <p>{description_collapse}</p>
        </div>
      </>
    );
  }

  if (type_collapse === "List") {
    return (
      <div className="kasa-collapse">
        <h3 className="kasa-collapse-title">{titre_collapse}</h3>
        <div className="kasa-collapse-chevron">
          <i className="fa-solid fa-chevron-up"></i>
        </div>
      </div>
    );
  }

  return true;
}

export default Collapse;
