import "./Collapse.css";
import { useState } from "react";

function Collapse(props) {
  const { titre_collapse, type_collapse, description_collapse } = props;
  /*const [isOpen, setOpen] = useState(false);

  const handleClick = () => {
    setOpen(!isOpen);
  }; rajouter onClick={handleClick}> juste après details <div className="kasa-collapse-chevron">
                    {isOpen ? (
                      <i className="fa-solid fa-chevron-down"></i>
                    ) : (
                      <i className="fa-solid fa-chevron-up"></i>
                    )}*/

  if (type_collapse === "Text") {
    return (
      <>
        <div className="kasa-collapse">
          <details className="kasa-collapse-details">
            <div className="kasa-collapse-details-logement">
              <div className="kasa-collapse-details-apropos">
                <summary>
                  <div className="kasa-collapse-title">{titre_collapse}</div>
                </summary>
                <div className="kasa-collapse">
                  <p className="kasa-collapse-texte">{description_collapse}</p>
                </div>
              </div>
            </div>
          </details>
        </div>
      </>
    );
  }

  /*if (type_collapse === "List") {
    return (
      <div className="kasa-collapse">
        <details className="kasa-collapse-details" onClick={handleClick}>
          <summary className="kasa-collapse-title">
            {titre_collapse}
            <div className="kasa-collapse-chevron">
              {isOpen ? (
                <i className="fa-solid fa-chevron-down"></i>
              ) : (
                <i className="fa-solid fa-chevron-up"></i>
              )}
            </div>
          </summary>
          <div className="kasa-collapse">
            <ul className="kasa-collapse-texte">
              {description_collapse.map((data, index) => (
                <li key={index}>{data}</li>
              ))}
            </ul>
          </div>
        </details>
      </div>
    );
  }*/

  return true;
}

export default Collapse;
